use std::time::Instant;
use itertools::Itertools;

static INPUT: &str = "";

fn part_one() -> String {
  let mut phase = INPUT.chars()
    .map(|c| (c as u8 - b'0') as i32)
    .collect_vec();
  for _ in 0..100 {
    for i in 1..=phase.len() {
      let mut index = i-1;
      let mut out = [0,0];
      while index < phase.len() {
        for o in 0..2 {
          for _ in 0..i {
            if index >= phase.len() { break; }
            out[o] += phase[index];
            index += 1;
          }
          index += i;
        }
      }
      phase[i-1] = (out[0] - out[1]).abs() % 10;
    }
  }
  phase[..8].iter()
    .map(|&i| (i as u8 + b'0') as char)
    .collect::<String>()
}

fn part_two() -> String {
  let offset: usize = INPUT[0..7].parse().unwrap();
  let mut phase = INPUT.chars()
    .cycle()
    .skip(offset % INPUT.len())
    .take(INPUT.len() * 10000 - offset)
    .map(|c| (c as u8 - b'0') as i32)
    .collect_vec();
  phase.reverse();
  for _ in 0..100 {
    let mut acc = 0;
    for i in 0..phase.len() {
      acc += phase[i];
      phase[i] = acc.abs() % 10;
    }
  }
  phase.iter()
    .rev()
    .take(8)
    .map(|&i| (i as u8 + b'0') as char)
    .collect::<String>()
}

fn main() {
  let now = Instant::now();
  println!("Part one: {}", part_one());
  println!("Part two: {}", part_two());
  println!("Time: {}ms", now.elapsed().as_millis());
}
